import React, { useState, useEffect } from "react";
import styled from "styled-components";

export default function Aep() {
  const [isOpen, setIsOpen] = useState(false);
  return (
    <>
      <NavSection
        isopen={isOpen == true ? "none" : "inherit"}
        fixed={isOpen == false ? "fixed" : ""}
        // animation={!isOpen ? "slide-top" : "slide-bottom"}
      >
        <NavContent>
          <NavLeft>
            <NavLink>
              <Image src={require("../Assets/images/images/Logo.png")} />
            </NavLink>
          </NavLeft>
          <NavRight>
            <NavLink>
              <Image
                onClick={() => (isOpen == true ? setIsOpen(false) : "")}
                src={require("../Assets/images/images/close.svg").default}
              />
            </NavLink>
          </NavRight>
        </NavContent>
        <NavContainer>
          <NaItems>
            <Itemimg>
              <Image
                src={require("../Assets/images/images/programs.svg").default}
              />
            </Itemimg>
            <NavItem>
              <NavHeading>
                <NavLink>Programs</NavLink>
              </NavHeading>
            </NavItem>
            <NavItem>
              <NavLink>School Students</NavLink>
            </NavItem>
            <NavItem>
              <NavLink>College Students</NavLink>
            </NavItem>
            <NavItem>
              <NavLink>Graduates or Dropouts</NavLink>
            </NavItem>
          </NaItems>
          <NaItems>
            <Itemimg>
              <Image
                src={require("../Assets/images/images/company.svg").default}
              />
            </Itemimg>
            <NavItem>
              <NavHeading>
                <NavLink>Company</NavLink>
              </NavHeading>
            </NavItem>
            <NavItem>
              <NavLink>About Us</NavLink>
            </NavItem>
            <NavItem>
              <NavLink>Contact Us</NavLink>
            </NavItem>
          </NaItems>
          <NaItems>
            <Itemimg>
              <Image
                src={require("../Assets/images/images/community.svg").default}
              />
            </Itemimg>
            <NavItem>
              <NavHeading>
                <NavLink>Community</NavLink>
              </NavHeading>
            </NavItem>
            <NavItem>
              <NavLink>Free Ground</NavLink>
            </NavItem>
            <NavItem>
              <NavLink>Challenges</NavLink>
            </NavItem>
          </NaItems>
        </NavContainer>
        <NavLinkBtn>Go to Dashboard</NavLinkBtn>
      </NavSection>

      <Header isopen={isOpen == true ? "none" : ""}>
        <Left>
          <SubHeading>
            <Link>
              <Image src={require("../Assets/images/images/Logo.png")} />
            </Link>
          </SubHeading>
        </Left>
        <Right>
          <Items>
            <Item>
              <Link>School Students</Link>
            </Item>
            <Item>
              <Link>College Students</Link>
            </Item>
            <Item>
              <Link>Graduates or Dropouts</Link>
            </Item>
            <Item>
              <LinkBtn>Go to Dashboard</LinkBtn>
            </Item>
          </Items>
          <Menu>
            <Link>
              <Image
                onClick={() => (isOpen == false ? setIsOpen(true) : "")}
                src={require("../Assets/images/images/menu.svg").default}
              />
            </Link>
          </Menu>
        </Right>
      </Header>
      <Section filter={isOpen == true ? "2px" : ""}>
        <SectionWrapeer>
          <Conatiner>
            <ConatinerLeft>
              <Heading>
                Creating Computer <Span>Engineers</Span> & Tech
                <Span>Scientists</Span>
              </Heading>
              <Content>
                Steyp is building a Digital University for students to learn and
                become Engineers and Future Scientists irrespective of their age
                or educational background. All that is required is the aptitude
                for the field.
              </Content>
            </ConatinerLeft>
            <ConatinerRight>
              <Imageplay src={require("../Assets/images/images/play.png")} />
              <Imagelogo
                src={require("../Assets/images/images/styepLogo.svg").default}
              />
              <ImageArrow
                src={require("../Assets/images/images/arrow.svg").default}
              />
              <Image src={require("../Assets/images/images/Main.png")} />
            </ConatinerRight>
          </Conatiner>
        </SectionWrapeer>
      </Section>
    </>
  );
}

const NavSection = styled.section`
  width: 100%;
  height: 100%;
  display: ${({ isopen }) => isopen};
  background-color: white;
  z-index: 100;
  position: absolute;
  top: 0px;
  left: 0;
  @keyframes slide-bottom {
    0% {
      -webkit-transform: translateY(0);
      transform: translateY(0);
    }
    100% {
      -webkit-transform: translateY(100px);
      transform: translateY(100px);
    }
  }

  @keyframes slide-top {
    0% {
      -webkit-transform: translateY(0);
      transform: translateY(0);
    }
    100% {
      -webkit-transform: translateY(-100px);
      transform: translateY(-100px);
    }
  }

  animation: ${({ animation }) => animation} 0.5s
    cubic-bezier(0.25, 0.46, 0.45, 0.94) both;

  @media all and (max-width: 980px) {
  }
`;

const NavContent = styled.div`
  display: flex;
  justify-content: space-evenly;
  margin-top: 50px;
`;
const NavLink = styled.a`
  display: block;
  cursor: pointer;
`;

const NavLeft = styled.div`
  width: 100px;
`;

const NavRight = styled.div`
  width: 20px;
`;

//  const NavImage = styled.img `
//     display: block;
//     width: 100%;`

const NavContainer = styled.div`
  display: flex;
  justify-content: space-evenly;
  gap: 100px;
  width: 80%;
  padding-bottom: 60px;
  margin-top: 50px;
  border-bottom: 1px solid rgb(210, 247, 234);
  background-color: white;
`;

const NaItems = styled.ul``;

const NavItem = styled.li`
  font-family: "Gordita_Regular";
  margin-bottom: 10px;
  color: rgb(156, 156, 156);
`;

const NavHeading = styled.h4`
  font-family: "Gordita_bold";
  color: rgb(112, 112, 112);
`;

const Itemimg = styled.div`
  width: 40px;
  margin-bottom: 20px;
  margin-left: 0px;
`;
const NavLinkBtn = styled.a`
  margin-top: 50px;
  display: block;
  color: rgb(78, 175, 124);
  border: 2px solid rgb(78, 175, 124);
  border-radius: 5px;
  width: 16%;
  font-family: "Gordita_bold";
  height: 20%;
  padding: 16px 0px;
  text-align: center;
  font-size: 16px;
  cursor: pointer;
`;

const Header = styled.header`
  margin: 0 auto;
  width: 100%;
  height: 80px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  display: ${({ isopen }) => isopen};
`;

const Left = styled.div`
  @media all and (max-width: 980px) {
    margin-left: 58px;
  }
  @media all and (max-width: 360px) {
    margin-left: 33px;
  }
`;

const SubHeading = styled.h1`
  width: 100px;
`;
const Image = styled.img`
  width: 100%;
  display: block;
`;

const Right = styled.div`
  @media all and (max-width: 980px) {
    margin-right: 11%;
    display: flex;
    justify-content: center;
  }
`;

const Items = styled.ul`
  display: flex;
  justify-content: space-between;
  align-items: center;
  @media all and (max-width: 860px) {
    display: none;
  }
`;

const Item = styled.li`
  margin-right: 30px;
  font-family: "Gordita_medium";
  color: rgb(112, 112, 112);
  &:last-child {
    margin-right: 0;
  }
`;

const Link = styled.a`
  cursor: pointer;
  &:hover {
    color: rgb(78, 175, 124);
  }
`;

const LinkBtn = styled.a`
  display: block;
  color: rgb(78, 175, 124);
  border: 2px solid rgb(78, 175, 124);
  border-radius: 5px;
  padding: 8px 20px;
  text-align: center;
  font-size: 16px;
  cursor: pointer;
  @media all and (max-width: 980px) {
    display: none;
  }
`;

const Section = styled.section`
  margin-top: 100px;
  width: 90%;
  z-index: -200;
  position: relative;
  filter: blur(${({ filter }) => filter});
  /* backdrop-filter: blur(${({ filter }) => filter}); */
  @media all and (max-width: 980px) {
    margin-top: 53px;
  }
  @media all and (max-width: 480px) {
    margin-top: 33px;
  }
`;

const SectionWrapeer = styled.section``;

const Conatiner = styled.div`
  width: 100%;
  display: flex;
  -webkit-box-align: center;
  align-items: center;
  -webkit-box-pack: justify;
  justify-content: space-between;
  margin-bottom: 60px;
  @media all and (max-width: 980px) {
    flex-direction: column;
  }
`;

const ConatinerLeft = styled.div`
  width: 45%;
  @media all and (max-width: 980px) {
    width: 100%;
    text-align: center;
  }
`;

const Span = styled.span`
  color: rgb(14, 159, 106);
  font-family: "Gordita_bold";
`;

const Content = styled.p`
  width: 100%;
  font-size: 18px;
  color: #868686;
  font-family: "Gordita_Regular";
  line-height: 30px;
  @media all and (max-width: 980px) {
    font-size: 15px;
    width: 95%;
  }
  @media (max-width: 480px) {
    margin-bottom: 20px;
    line-height: 20px;
  }
  @media (max-width: 360px) {
    font-size: 10px;
    line-height: 17px;
  }
`;

const Heading = styled.h1`
  margin-bottom: 30px;
  font-size: 45px;
  width: 100%;
  font-family: "Gordita_medium";
  @media all and (max-width: 1280px) {
    font-size: 40px;
  }
  @media all and (max-width: 980px) {
    width: 82%;
    font-size: 32px;
    margin-bottom: 10px;
  }
  @media all and (max-width: 860px) {
    width: 85%;
  }
  @media all and (max-width: 480px) {
    font-size: 25px;
  }
`;

const ConatinerRight = styled.div`
  width: 47%;
  position: relative;
  @media all and (max-width: 980px) {
    width: 83%;
  }
`;

const Imageplay = styled.img`
  position: absolute;
  left: 40%;
  top: 44%;
  width: 90px;
  z-index: 3;
  @media all and (max-width: 980px) {
    top: 40%;
    left: 31%;
    width: 100px;
  }
  @media (max-width: 768px) {
    top: 42%;
    left: 31%;
    width: 68px;
  }
  @media (max-width: 480px) {
    width: 48px;
  }
  @media (max-width: 360px) {
    width: 39px;
  }
`;
const Imagelogo = styled.img`
  position: absolute;
  top: 41%;
  left: 8%;
  width: 40%;
  @media all and (max-width: 980px) {
    top: 41%;
    left: 6%;
    width: 30%;
  }
`;

const ImageArrow = styled.img`
  position: absolute;
  left: 46%;
  top: 58%;
  z-index: 4;
  @media all and (max-width: 980px) {
    left: 39%;
    top: 49%;
  }
  @media (max-width: 768px) {
    left: 37%;
    top: 52%;
    width: 9%;
  }
  @media (max-width: 640px) {
    left: 38%;
    top: 55%;
  }
`;

const Menu = styled.div`
  width: 30px;
  display: none;
  @media all and (max-width: 980px) {
    display: block;
    width: 25px;
  }
`;
