import React, { Component } from "react";
// import styled from "styled-components";


class ToDo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            input: ""
        };
    };
    deleteItem = (id) => {
        let new_item = this.state.items.filter((item) => item.id !== id);
        this.setState({
            items:[...this.state.inew_item]
        })
    };
    renderItems = () => {
        return this.state.items.map((item) => (
            <li key={item.id}>
                {item.title} <button onClick={() => this.deleteItem(item.id)}>Delete</button>
            </li>
        ));
    };

    updateItem = () => {
        let new_item = {
            id: this.state.items.length,
            title: this.state.input
        }
        if (this.state.input) {
            this.setState({
                items:[...this.state.items, new_item],
                input: ""
            })
        }
        
    }
    render() {
        return (
            <>
                <h1>My To Do List</h1>
                <ul>
                    {this.renderItems()}
                </ul>

                <input placeholder="new item" value={this.state.input} onChange={(e) => this.setState({ input: e.target.value})}/>
                <button onClick={this.updateItem}>Add new item</button>
            </>
        )
    }
}

export default ToDo
