import React, { useState } from 'react'
import styled from 'styled-components'
import '../../App.css'
import Image from '../Assets/images/images/decorator.svg'

export default function LoginPage() {
  const [isOpen, setIsOpen] = useState(false)
  return (
    <Section isopen={isOpen == false? "none": ""}>
        <RightContainer>

          <Close onClick={() => isOpen == true? setIsOpen(false): setIsOpen}><Images  src={require('../Assets/images/images/close-icon.svg').default}/></Close>
          <ImageContainer>
          <Items>
              <Heading>Login to your account</Heading>
              <Text>Enter your registered phone number</Text>
              <Container>
                <Item>
                <ImageOne src={require('../Assets/images/images/Ind.png')}/>
                <ImageTwo src={require('../Assets/images/images/Arrow.png')}/>
                <ImageThree src={require('../Assets/images/images/phone.svg').default}/>
                <Input placeholder='Enter your phone number' type='number'/>
                </Item>
              </Container>
              <Button>Next</Button>
              <Texts>New to Steyp? <Link>Create an account</Link></Texts>
            </Items>
            <BottomConatiner> <TextsBottom>Terms of service</TextsBottom></BottomConatiner>
          </ImageContainer>
        </RightContainer>
    </Section>
  )
}

const Section = styled.section`
    position: fixed;
    display: ${({isopen}) => isopen};
    height: 100%;
    width: 100%;
    background: rgba(0, 0, 0, 0.6);
    z-index: 5000;`;
const RightContainer = styled.div`
  z-index: 500;
  background: rgb(255, 255, 255);
  width: 600px;
  position: absolute;
  right: 0px;
  top: 0px;
  bottom: 0px;
  height: 100%;`;

const ImageContainer = styled.div`
  position: relative;
  width: 100%;
  height: 100%;
  overflow-y: scroll;
  background: url(${Image}) 0% 0% / contain no-repeat;
  background-repeat: no-repeat;
  padding: 0px 60px;` 
const Close = styled.span`
  font-size: 34px;
  color: rgb(255, 152, 0);
  position: absolute;
  left: -42px;
  top: 11px;
  cursor: pointer;
` 
const Images = styled.img`
 width: 30px;
`
const Items = styled.div`
  padding-top: 45%; `
const Heading = styled.h4`
  font-size:23px;
  font-family: 'Gordita_Regular';
  font-weight:800;
  margin-bottom: 20px;`;
  
const Text = styled.p`
  font-family: 'Gordita_Regular';
  font-size: 14px;
  color: #868686;
  margin-bottom: 30px;`;
const Container = styled.div`
  display: flex;
  justify-content:center;
  width: 100%;
  position:relative;`;
const Item = styled.div`
  display: block;`;
const Input = styled.input`
  border:1px solid black;
  padding:17px 50px;
  border-radius: 5px;
  width: 350px;
  ::-webkit-inner-spin-button
  {
  -webkit-appearance: none;
  } `;
const ImageOne = styled.img`
  width: 40px;
  object-fit: cover;
  height: 40px;
  border-radius: 50%;
  position:absolute;
  left:0px;
  top: 7px;
  cursor: pointer;`;
  
const ImageTwo = styled.img`
  margin-right:10px;
  width: 10px;
  cursor: pointer;` ;
const ImageThree = styled.img`
  position:absolute;
  top:13px;
  left:80px;
  width: 13px;`;   
const Button = styled.button`
  cursor: pointer;
  background: rgb(92, 198, 106);
  border-radius: 6px;
  height: 48px;
  color:white;
  width:90%;
  font-weight: bold;
  font-size: 16px;
  margin-top: 30px;`;
const Texts = styled.p`
  font-family: 'Gordita_Regular';
  font-weight: bold;
  margin-top:15px;
  text-align:center;
  font-size:13px;` ;
const Link = styled.a`
  font-family: 'Gordita_Regular';
  font-weight: bold;
  cursor: pointer;
  color: rgb(92, 198, 106);`
const BottomConatiner = styled.div`
  border-top:1px solid rgb(209, 219, 218);
  margin-top: 180px;` ;
const TextsBottom = styled.p`
   font-family: 'Gordita_Regular';
   text-align: center;
   font-size:13px;
   margin-top: 20px;
   font-weight:bold`;
